# 登录校验import timedef decorator(func):    def wrapper(*args, **kwargs):        print('正在校验...')        time.sleep(1)        print('校验完毕。')        func(*args, **kwargs)    return wrapper@decoratordef f1(n):    print('-------f1--------', n)@decoratordef f2(name, age):    print('-------f2--------', name, age)@decoratordef f3(students, clazz='1905'):    print('{}班的学生如下：'.format(clazz))    for s in students:        print(s)f1(2)f2('nieguozhang', 25 )f3(['1', '2', '3'], clazz='1904')@decoratordef f4():    print('----f4')f4()