# 装饰器的应用# 开发：登录验证。import timeislogin = False  # 默认是没有登录的# 定义一个登录函数def login():    username = input('输入用户名：')    password = input('输入密码：')    if username == 'admin' and password == '123456':        return True    return False# 定义一个装饰器，进行付款验证def login_required(func):    def wrapper(*args, **kwargs):        global islogin        print('--------付款--------')        if islogin == False:            print('您没有登录，请登录账号。')            islogin = login()            # print('result:', result)            # 跳转至登录界面        else:            func(*args, **kwargs)    return wrapper@login_requireddef pay(money):    print('正在付款，付款金额是:{}元'.format(money))    print('付款中...')    time.sleep(2)    print('付款完成！')pay(10000)pay(8000)