# 持久化保存：文件# list tuple dict 都不是持久化保存数据，都在内存import os# 用户注册def register():    username = input('输入用户名:')    pwd = input('输入密码：')    repwd = input('输入你的确认密码：')    if pwd == repwd:        # 保存信息        path = os.path.dirname(__file__)        users_path = os.path.join(path, 'book/users.txt')        with open(users_path, 'a') as wstream:            wstream.write('{} {}\n'.format(username, pwd))        print('用户注册成功！')    else:        print('您两次输入的密码不一致，请重新输入！')def login():    username = input('输入用户名:')    pwd = input('输入密码:')    if username and pwd:        path = os.path.dirname(__file__)        users_path = os.path.join(path, 'book/users.txt')        users = []        with open(users_path, 'r') as rstream:            while True:                user = rstream.readline()  # admin 123456\n                if not user:                    print('用户名或者密码错误！')                    login()                    break                input_user = '{} {}\n'.format(username, pwd)                if input_user == user:                    print('用户登陆成功！')                    breakdef show_books():    print('--------图书馆里的图书有：----------')    path = os.path.dirname(__file__)    books_path = os.path.join(path, 'book/books.txt')    with open(books_path, 'r') as rstream:        books = rstream.readlines()        for book in books:            print(book, end='')def main():    print('=========图书管理系统=========')    while True:        msg = input('请选择操作\n 1.注册\n 2.登录\n 3.显示图书\n 4.退出系统\n')        if msg == '1':            register()        elif msg == '2':            login()        elif msg == '3':            show_books()        elif msg == '4':            breakif __name__ == '__main__':    # login()    main()