def func():    try:        n1 = int(input('输入第一个数字：'))        n2 = int(input('输入第二个数字：'))        # +加法        operate = input('输入运算符(+-*/)：')        result = 0        if operate == '+':            result = n1 + n2        elif operate == '-':            result = n1 - n2        elif operate == '*':            result = n1 * n2        elif operate == '/':            result = n1 / n2        else:            print('符号输入错误！')            return        print('结果是:', result)        # 文件操作        with open('result.txt', 'w') as wstream:            wstream.write('本次的运算结果是:{}'.format(result))        with open('aa.txt', 'r') as rstream:            print(rstream.read())    except ZeroDivisionError:        print('除数不能为0！')    except ValueError:        print('必须输入数字！')    except FileNotFoundError:        print('没有找到当前文件：'.format('aa.txt'))if __name__ == '__main__':    func()