class Student:    # __age = 18  # 类属性    def __init__(self, name, age):        self.__name = name        self.__age = age        self.__score = 59  # 私有化    # 定义公有的set和get方法    def setAge(self, age):        if age > 0 and age <= 120:            self.__age = age        else:            print('年龄不在规定的范围内。')    def getAge(self):        return self.__age    def setName(self, name):        if len(name) == 6:            self.__name = name        else:            print('名字长度不是6位')    def getName(self):        return self.__name    def __str__(self):        return '姓名:{},年龄:{},分数:{}'.format(self.__name, self.__age, self.__score)s = Student('Nieguozhang', 25)print(dir(Student))print(dir(s))print(s._Student__age)  # 其实就是s.__age__,只不过是系统自动改名