# 继承：is a , has aclass Road:    def __init__(self, name, length):        self.name = name        self.length = lengthclass Car:    def __init__(self, name, speed):        self.name = name        self.speed = speed    def get_time(self, road):        print('{}在{}上以{}的速度行驶了{}'.format(self.name, road.name,                                         self.speed, road.length))    def __str__(self):        return '车名:{},速度:{}'.format(self.name, self.speed)if __name__ == '__main__':    car = Car('奥迪A4', '120km/h')    road = Road('京港澳高速', '2000km')    car.get_time(road)    print(car)