class Person:    def __init__(self, name, age):        self.name = name        self.age = age    def eat(self):        print(self.name + '正在吃饭。')    def run(self):        print(self.name + '正在跑步。')class Student(Person):    def __init__(self, name, age, clazz):        super(Student, self).__init__(name, age)        self.clazz = clazz    def study(self, course):        print('{}正在学习{}课程'.format(self.name, course))    def eat(self):        print(self.name + '正在吃饭....，喜欢吃：狮子头盖饭。')class Employee(Person):    def __init__(self, name, age, salary, manager):        super().__init__(name, age)        self.salary = salary        self.manager = managerclass Doctor(Person):    def __init__(self, name, age, patients):        super(Doctor, self).__init__(name, age)stu = Student('聂国章', 25, '计算机研1801')stu.run()stu.eat()stu.study('Python基础课程')e = Employee('聂国章', 25, 10000, '聂国章')e.run()d = Doctor('聂国章', 25, ['aa', 'bb', 'cc'])d.run()