class Person:    def __init__(self, no, name, salary):        self.no = no        self.name = name        self.salary = salary    def __str__(self):        msg = '工号:{},姓名:{},本月工资:{}'.format(self.no, self.name, self.salary)        return msg    def calculate_salary(self):        return self.salaryclass Worker(Person):    def __init__(self, no, name, salary, hours, per_hour):        super(Worker, self).__init__(no, name, salary)        self.hours = hours        self.per_hour = per_hour    def calculate_salary(self):        money = self.hours * self.per_hour        self.salary += money        return self.salaryclass Saleman(Person):    def __init__(self, no, name, salary, salemoney, percentage):        super(Saleman, self).__init__(no, name, salary)        self.salemoney = salemoney        self.percent = percentage    def calculate_salary(self):        money = self.salemoney * self.percent        self.salary += money        return self.salary# 创建对象w = Worker('001', 'king', 2000, 160, 50)s = w.calculate_salary()print('月薪：', s)print(w)print('-' * 30)saler = Saleman('002', 'lucy', 5000, 5000000, 0.003)print('月薪是：', saler.calculate_salary())print(saler)