# 多态class Person:    role = 'Pet'    def __init__(self, name):        self.name = name    def feed(self, pet):        # instance(obj, 类) 判断obj是不是该类的对象或者判断obj是不是该类子类的对象        if isinstance(pet, Pet):  # pet既可以接收cat, 也可以接收dog,还可以接收tiger            print('{}喜欢养宠物:{}, 昵称是:{}'.format(self.name, pet.role, pet.nickname))        else:            print('不是宠物类型的....')class Pet:    role = 'Pet'    def __init__(self, nickname, age):        self.nickname = nickname        self.age = age    def show(self):        print('昵称：{}, 年龄:{}'.format(self.nickname, self.age))class Cat(Pet):    role = '猫'    def catch_mouse(self):        print('抓老鼠....')class Dog(Pet):    role = '狗'    def watch_house(self):        print('看家高手...')class Tiger:    def eat(self):        print('太可怕了，可以吃人...')# 创建对象cat = Cat('花花', 2)dog = Dog('小黄', 4)person = Person('聂国章')tiger = Tiger()person.feed(cat)person.feed(dog)person.feed(tiger)