# class Person:#     def __init__(self, name):#         self.name = name##     def eat(self):#         print('-------->eat1')##     def eat(self, food):#         print('-------->eat:', food)### p = Person('聂国章')# p.eat()# p.eat('红烧鱼')# 继承中的多继承class Base:    def test(self):        print('BASE')class A(Base):    def test(self):        print('A' * 20)class B(Base):    def test(self):        print('B' * 20)class C(Base):    def test(self):        print('C' * 20)class D(A, B, C):    pass    # def test(self):    #     print('D' * 20)d = D()d.test()# 查找搜索顺序import inspectprint(inspect.getmro(D))print(D.__mro__)'''python允许多继承父类中有相同方法，搜索的时候遵循广度优先。'''