# 单例模式# 开发模式: 单例模式# class Student:#     pass### s = Student()# s1 = Student()## print(s)# print(s1)class Singleton:    # 私有化 单例的地址就存在于__instance中    __instance = None    name = 'Jack'    # 重写父类中的__new__    def __new__(cls):        print('-------> __new__')        if cls.__instance is None:            cls.__instance = object.__new__(cls)        return cls.__instance    def show(self):        print('------>', Singleton.name)s = Singleton()s1 = Singleton()print(s)print(s1)s.show()s1.show()