from multiprocessing import Processimport timeimport osdef task1(s):    while True:        time.sleep(s)        print('这是任务1。。。。。。。', os.getpid(), '-' * 5, os.getppid())def task2(s):    while True:        time.sleep(s)        print('这是任务2。。。。。。。', os.getpid(), '-' * 5, os.getppid())number = 1if __name__ == '__main__':    # 子进程    p1 = Process(target=task1, name='任务1', args=(2,))    p1.start()    p2 = Process(target=task2, name='任务2', args=(1,))    p2.start()    while True:        number += 1        time.sleep(0.2)        if number == 100:            p1.terminate()            p2.terminate()            break        else:            print(number)    # 主进程打印任务    print('-' * 20)    print('-' * 20)    print(os.getpid())