"""进程之间的数据默认是不共享的，无论是可变数据类型还是不可变数据类型，都是不共享的"""import timefrom multiprocessing import Processm = 1  # 不可变数据类型list1 = []  # 可变数据类型def task1(s):    global m    while True:        time.sleep(s)        m += 1        list1.append(str(m) + 'task1')        print('这是任务1。。。。。。。', m, list1)def task2(s):    global m    while True:        time.sleep(s)        m += 1        list1.append(str(m) + 'task2')        print('这是任务2。。。。。。。', m, list1)if __name__ == '__main__':    # 子进程    p1 = Process(target=task1, name='任务1', args=(2,))    p1.start()    p2 = Process(target=task2, name='任务2', args=(1,))    p2.start()    while True:        time.sleep(1)        m += 1        list1.append(str(m) + '主进程')        print('主进程中', m, list1)    # 主进程打印任务    print('-' * 20)    print('-' * 20)