"""网络访问案例"""import requestsimport geventfrom gevent import monkeyimport urllib.request# import ssl# ssl._create_default_https_context = ssl._create_unverified_contextmonkey.patch_all()import sslssl._create_default_https_context = ssl._create_unverified_contextdef download(url):    response = urllib.request.urlopen(url)  # 耗时操作    content = response.read()    print('下载了{}的数据，长度:{}'.format(url, len(content)))if __name__ == '__main__':    urls = ['http://www.163.com', 'http://www.qq.com', 'http://www.whut.edu.cn']    g1 = gevent.spawn(download, urls[0])    g2 = gevent.spawn(download, urls[1])    g3 = gevent.spawn(download, urls[2])    g1.join()    g2.join()    g3.join()