import pymysqldef main():    no = int(input('要删除的部门编号：'))    # 创建连接对象    conn = pymysql.connect(host='127.0.0.1', port=3306,                           user='root', password='123456',                           database='HR', charset='utf8')    # print(conn)    # 获取游标，with上下文语法    try:        with conn.cursor() as cursor:            sql = 'delete from tb_dept where dno={}'.format(no)            result = cursor.execute(sql)            # 输出为1表示影响了一行，则成功            if result == 1:                print('删除成功')                # 操作成功则提交事务            conn.commit()  # 提交事务    except pymysql.MySQLError as error:        print(error)        conn.rollback()  # 失败则回滚    finally:        conn.close()  # 关闭连接，避免浪费资源if __name__ == '__main__':    main()