-- 删除学生表drop database if exists school;-- 创建学生表，并指定默认字符集create database school default charset utf8;-- 切换数据库上下文use school;-- 删除学生表drop table if exists tb_student;-- 创建学生表create table tb_student(stuid int not null comment '学号',stuname varchar(20) not null comment '姓名',stusex bit default 1 comment '性别',stubirth date comment '生日',primary key (stuid));-- 修改学生表  增加列alter table tb_student add column stuaddr varchar(255) comment '住址';-- 修改列的字段长度alter table tb_student change stuaddr stuaddr varchar(511);-- 删除列alter table tb_student drop column stuaddr;