import redisdef main():    # 创建连接对象    client = redis.Redis(host='175.24.127.108', port=6379,                         password='123456')    client.flushall()    # print(client)    # key是username， value是Python学习者，将键值对存入Redis缓存，并设置存活时间为30s    client.set('username', 'Python学习者', ex=30)    print(client.ttl('username'))  # 查看键值对时间    # 默认是字节串，如果需要转化为字符串需要解码decode()    print(client.get('username'))    print(client.get('username').decode())    # Hash    client.hset('stu1', 'id', '1001')    client.hset('stu1', 'name', 'Python学习者')    # 打印出来的是字典    print(client.hgetall('stu1'))    print(client.hget('stu1', 'name').decode())    # 列表    client.rpush('list1', 10, 20, 30, 40)    print(client.lrange('list1', 0, -1))if __name__ == '__main__':    main()